function [condition, l1, l2, xei, yei, zei, xp, yp, zp, al, a, b, c] = condition(l1, l2, xei, yei, zei, xp, yp, zp, al)
a = 2*l2^2*xei^2 + 2*l2^2*xp^2 + 2*l2^2*yei^2 + 2*l2^2*yp^2 + 2*l1^2*zei^2 + 2*l2^2*zei^2 + 2*l1^2*zp^2 + 2*l2^2*zp^2 - 6*xei^2*xp^2 - 2*xei^2*yei^2 - 2*xei^2*yp^2 - 2*xp^2*yei^2 - 2*xp^2*yp^2 - 2*xei^2*zei^2 - 2*xei^2*zp^2 - 2*xp^2*zei^2 - 6*yei^2*yp^2 - 2*xp^2*zp^2 - 2*yei^2*zei^2 - 2*yei^2*zp^2 - 2*yp^2*zei^2 - 2*yp^2*zp^2 - 6*zei^2*zp^2 + 4*xei*xp^3 + 4*xei^3*xp + 4*yei*yp^3 + 4*yei^3*yp + 4*zei*zp^3 + 4*zei^3*zp - l1^4 - l2^4 - xei^4 - xp^4 - yei^4 - yp^4 - zei^4 - zp^4 + 2*l1^2*l2^2 - 4*l2^2*xei*xp - 4*l2^2*yei*yp - 4*l1^2*zei*zp - 4*l2^2*zei*zp + 2*l1^2*xei^2*cos(2*al) + 2*l1^2*xp^2*cos(2*al) - 2*l1^2*yei^2*cos(2*al) - 2*l1^2*yp^2*cos(2*al) + 4*xei*xp*yei^2 + 4*xei*xp*yp^2 + 4*xei*xp*zei^2 + 4*xei^2*yei*yp + 4*xei*xp*zp^2 + 4*xp^2*yei*yp + 4*yei*yp*zei^2 + 4*xei^2*zei*zp + 4*yei*yp*zp^2 + 4*xp^2*zei*zp + 4*yei^2*zei*zp + 4*yp^2*zei*zp - 4*l1^2*xei*xp*cos(2*al) + 4*l1^2*yei*yp*cos(2*al) - 8*xei*xp*yei*yp - 8*xei*xp*zei*zp - 8*yei*yp*zei*zp + 4*l1^2*xei*yei*sin(2*al) - 4*l1^2*xei*yp*sin(2*al) - 4*l1^2*xp*yei*sin(2*al) + 4*l1^2*xp*yp*sin(2*al);
b = xei^2*cos(al)^2 - 2*zei*zp + xp^2*cos(al)^2 + yei^2*sin(al)^2 + yp^2*sin(al)^2 + zei^2 + zp^2 - 2*xei*xp*cos(al)^2 + xei*yei*sin(2*al) - xei*yp*sin(2*al) - xp*yei*sin(2*al) + xp*yp*sin(2*al) - 2*yei*yp*sin(al)^2;
c = xei*cos(al) - xp*cos(al) + yei*sin(al) - yp*sin(al);
condition = a >= 0 & b ~= 0 & c ~= 0;
end
